mapper.update:
UPDATE MYGROUP SET GTITLE = #{gtitle}, GCONTENT = #{gcontent}, GPEOPLE = #{gpeople}, GLANGUAGE1 = #{glanguage1}, GLANGUAGE2 = #{glanguage2}, GLANGUAGE3 = #{glanguage3},
GDEV = #{gdev}, GDESIGN = #{gdesign}, GPM = #{gpm},
GSDATE = TO_DATE(TO_CHAR(#{gsdate}), 'YYYY-MM-DD'), GFDATE = TO_DATE(TO_CHAR(#{gfdate}), 'YYYY-MM-DD'), GLOC = #{gloc} WHERE GID = #{gid}

mapper.select:
SELECT S.GID, S.GTITLE, S.GSDATE, S.GFDATE, H.GSSTATUS GSSTATUS 
	FROM MYGROUP S, GSTATUS H 
	WHERE S.GID = H.GID AND (H.GSSTATUS = 2 OR H.GSSTATUS = 1) AND S.GCHARACTER = 'S' AND H.MID = #{mid}

SELECT S.GID, S.GTITLE, S.GSDATE, S.GFDATE, H.GSSTATUS GSSTATUS 
	FROM MYGROUP S, GSTATUS H 
	WHERE S.GID = H.GID AND (H.GSSTATUS = 2 OR H.GSSTATUS = 1) AND S.GCHARACTER = 'P' AND H.MID = #{mid}

SELECT S.GCHARACTER, S.GID, S.GTITLE, S.GSDATE, S.GFDATE, H.GSSTATUS 
	FROM MYGROUP S, GSTATUS H 
	WHERE S.GID = H.GID AND (H.GSSTATUS = 3) AND H.MID = #{mid}
